import{_ as i}from"./chunks/ArticleMetadata.4a1a58d6.js";import{_ as o,C as r,o as t,c,H as d,w as u,k as n,a as h,Q as b,b as m,e as g}from"./chunks/framework.144d84a9.js";import"./chunks/md5.3e7612d8.js";const R=JSON.parse('{"title":"虚拟机 ubuntu16.04 与 ROS-kinetic","description":"","frontmatter":{"title":"虚拟机 ubuntu16.04 与 ROS-kinetic","date":"2020-12-28 19:52","categories":["note"],"tags":["ubuntu16.04","ROS-kinetic"],"copyright":true},"headers":[],"relativePath":"note/2020/12/28/Virtual_machine_ubuntu16-04_with_ROS-kinetic.md","filePath":"note/2020/12/28/Virtual_machine_ubuntu16-04_with_ROS-kinetic.md","lastUpdated":1711185179000}'),_={name:"note/2020/12/28/Virtual_machine_ubuntu16-04_with_ROS-kinetic.md"},k=n("h1",{id:"虚拟机-ubuntu16-04-与-ros-kinetic-的安装",tabindex:"-1"},[h("虚拟机 ubuntu16.04 与 ROS-kinetic 的安装 "),n("a",{class:"header-anchor",href:"#虚拟机-ubuntu16-04-与-ros-kinetic-的安装","aria-label":'Permalink to "虚拟机 ubuntu16.04 与 ROS-kinetic 的安装"'},"​")],-1),y=b('<h3 id="背景介绍" tabindex="-1">背景介绍 <a class="header-anchor" href="#背景介绍" aria-label="Permalink to &quot;背景介绍&quot;">​</a></h3><p>​最近由于要做的一个项目涉及到 ROS 与机器视觉以及一些 SLAM 技术，所以学习了一下机器人操作系统 ROS 在 ubuntu 上的安装，由于开始时经验不足，为了能有更高的容错率，我选择在 Virtual Box 上安装 Ubuntu16.04 虚拟机，得益于虚拟机可以轻松进行备份和恢复的优势，我在进行 ROS 的安装时更有信心。</p><h2 id="本机环境" tabindex="-1">本机环境 <a class="header-anchor" href="#本机环境" aria-label="Permalink to &quot;本机环境&quot;">​</a></h2><p>​Windows10 家庭版 + Ubuntu16.04 虚拟机 + ROS kinetic</p><h2 id="方案选择" tabindex="-1">方案选择 <a class="header-anchor" href="#方案选择" aria-label="Permalink to &quot;方案选择&quot;">​</a></h2><h3 id="方案-1-vmware-虚拟机安装-ubuntu16-04" tabindex="-1">方案 1.VMware 虚拟机安装 ubuntu16.04 <a class="header-anchor" href="#方案-1-vmware-虚拟机安装-ubuntu16-04" aria-label="Permalink to &quot;方案 1.VMware 虚拟机安装 ubuntu16.04&quot;">​</a></h3><p>可以参考火耳软件安装的<a href="http://mp.weixin.qq.com/s?__biz=MzU0MTg5NDkzNA==&amp;mid=2247498154&amp;idx=1&amp;sn=a2947b925ffb530d7690149c3b3f2232&amp;chksm=fb2049d3cc57c0c5743da147a8b8408eb73ce61262f833eb6ecca7fb433a347241a08e2273c7&amp;mpshare=1&amp;scene=23&amp;srcid=1228eMZg5msv2v0islQnklYv&amp;sharer_sharetime=1609158326393&amp;sharer_shareid=970e485b437aa6a7a3cae61fe48119bd#rd" target="_blank" rel="noreferrer">这篇文章</a></p><h3 id="方案-2-virtual-box-虚拟机安装-ubuntu16-04" tabindex="-1">方案 2.Virtual Box 虚拟机安装 ubuntu16.04 <a class="header-anchor" href="#方案-2-virtual-box-虚拟机安装-ubuntu16-04" aria-label="Permalink to &quot;方案 2.Virtual Box 虚拟机安装 ubuntu16.04&quot;">​</a></h3><p>可以参考博客园上的<a href="https://www.cnblogs.com/luengmingbiao/p/10859905.html" target="_blank" rel="noreferrer">这篇文章</a></p><h2 id="安装流程" tabindex="-1">安装流程 <a class="header-anchor" href="#安装流程" aria-label="Permalink to &quot;安装流程&quot;">​</a></h2><p><a href="http://wiki.ros.org/melodic/Installation/Ubuntu" target="_blank" rel="noreferrer">ROS 官方安装教程</a></p><h3 id="_1-注意对应好版本" tabindex="-1">1.注意对应好版本 <a class="header-anchor" href="#_1-注意对应好版本" aria-label="Permalink to &quot;1.注意对应好版本&quot;">​</a></h3><p><img src="https://cn-sy1.rains3.com/dfdfgf/blog/Virtual_machine_ubuntu16-04_with_ROS-kinetic/ROS_Installation_Options.jpg" alt=""></p><h3 id="_2-配置软件源" tabindex="-1">2.配置软件源 <a class="header-anchor" href="#_2-配置软件源" aria-label="Permalink to &quot;2.配置软件源&quot;">​</a></h3><p>ROS 官方默认安装源：</p><div class="language-shell vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki github-dark-dimmed vp-code-dark"><code><span class="line"><span style="color:#F69D50;">sudo</span><span style="color:#ADBAC7;"> </span><span style="color:#96D0FF;">sh</span><span style="color:#ADBAC7;"> </span><span style="color:#6CB6FF;">-c</span><span style="color:#ADBAC7;"> </span><span style="color:#96D0FF;">&#39;echo &quot;deb http://packages.ros.org/ros/ubuntu $(lsb_release -sc) main&quot; &gt; /etc/apt/sources.list.d/ros-latest.list&#39;</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#6F42C1;">sudo</span><span style="color:#24292E;"> </span><span style="color:#032F62;">sh</span><span style="color:#24292E;"> </span><span style="color:#005CC5;">-c</span><span style="color:#24292E;"> </span><span style="color:#032F62;">&#39;echo &quot;deb http://packages.ros.org/ros/ubuntu $(lsb_release -sc) main&quot; &gt; /etc/apt/sources.list.d/ros-latest.list&#39;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>国内 ROS 源：</p><p>重庆大学镜像源</p><div class="language-shell vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki github-dark-dimmed vp-code-dark"><code><span class="line"><span style="color:#F69D50;">sudo</span><span style="color:#ADBAC7;"> </span><span style="color:#96D0FF;">sh</span><span style="color:#ADBAC7;"> </span><span style="color:#6CB6FF;">-c</span><span style="color:#ADBAC7;"> </span><span style="color:#96D0FF;">&#39;. /etc/lsb-release &amp;&amp; echo &quot;deb https://mirrors.cernet.edu.cn/ros/ubuntu/ `lsb_release -cs` main &gt; /etc/apt/sources.list.d/ros-latest.list&#39;</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#6F42C1;">sudo</span><span style="color:#24292E;"> </span><span style="color:#032F62;">sh</span><span style="color:#24292E;"> </span><span style="color:#005CC5;">-c</span><span style="color:#24292E;"> </span><span style="color:#032F62;">&#39;. /etc/lsb-release &amp;&amp; echo &quot;deb https://mirrors.cernet.edu.cn/ros/ubuntu/ `lsb_release -cs` main &gt; /etc/apt/sources.list.d/ros-latest.list&#39;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>清华的安装源</p><div class="language-shell vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki github-dark-dimmed vp-code-dark"><code><span class="line"><span style="color:#F69D50;">sudo</span><span style="color:#ADBAC7;"> </span><span style="color:#96D0FF;">sh</span><span style="color:#ADBAC7;"> </span><span style="color:#6CB6FF;">-c</span><span style="color:#ADBAC7;"> </span><span style="color:#96D0FF;">&#39;. /etc/lsb-release &amp;&amp; echo &quot;deb http://mirrors.tuna.tsinghua.edu.cn/ros/ubuntu/ `lsb_release -cs` main&quot; &gt; /etc/apt/sources.list.d/ros-latest.list&#39;</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#6F42C1;">sudo</span><span style="color:#24292E;"> </span><span style="color:#032F62;">sh</span><span style="color:#24292E;"> </span><span style="color:#005CC5;">-c</span><span style="color:#24292E;"> </span><span style="color:#032F62;">&#39;. /etc/lsb-release &amp;&amp; echo &quot;deb http://mirrors.tuna.tsinghua.edu.cn/ros/ubuntu/ `lsb_release -cs` main&quot; &gt; /etc/apt/sources.list.d/ros-latest.list&#39;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>中科大的安装源</p><div class="language-shell vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki github-dark-dimmed vp-code-dark"><code><span class="line"><span style="color:#F69D50;">sudo</span><span style="color:#ADBAC7;"> </span><span style="color:#96D0FF;">sh</span><span style="color:#ADBAC7;"> </span><span style="color:#6CB6FF;">-c</span><span style="color:#ADBAC7;"> </span><span style="color:#96D0FF;">&#39;. /etc/lsb-release &amp;&amp; echo &quot;deb http://mirrors.ustc.edu.cn/ros/ubuntu/ `lsb_release -cs` main&quot; &gt; /etc/apt/sources.list.d/ros-latest.list&#39;</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#6F42C1;">sudo</span><span style="color:#24292E;"> </span><span style="color:#032F62;">sh</span><span style="color:#24292E;"> </span><span style="color:#005CC5;">-c</span><span style="color:#24292E;"> </span><span style="color:#032F62;">&#39;. /etc/lsb-release &amp;&amp; echo &quot;deb http://mirrors.ustc.edu.cn/ros/ubuntu/ `lsb_release -cs` main&quot; &gt; /etc/apt/sources.list.d/ros-latest.list&#39;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>使用<code>alinyun</code>镜像软件源即可</p><p><img src="https://cn-sy1.rains3.com/dfdfgf/blog/Virtual_machine_ubuntu16-04_with_ROS-kinetic/apply_install_sources.jpg" alt=""></p><h3 id="_3-添加密钥" tabindex="-1">3.添加密钥 <a class="header-anchor" href="#_3-添加密钥" aria-label="Permalink to &quot;3.添加密钥&quot;">​</a></h3><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki github-dark-dimmed vp-code-dark"><code><span class="line"><span style="color:#adbac7;">sudo apt-key adv --keyserver &#39;hkp://keyserver.ubuntu.com:80&#39; --recv-key C1CF6E31E6BADE8868B172B4F42ED6FBAB17C654</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#24292e;">sudo apt-key adv --keyserver &#39;hkp://keyserver.ubuntu.com:80&#39; --recv-key C1CF6E31E6BADE8868B172B4F42ED6FBAB17C654</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p><img src="https://cn-sy1.rains3.com/dfdfgf/blog/Virtual_machine_ubuntu16-04_with_ROS-kinetic/add_key.jpg" alt=""></p><h3 id="_4-安装-ros-桌面完整版-desktop-full" tabindex="-1">4.安装 ROS（桌面完整版 Desktop-Full） <a class="header-anchor" href="#_4-安装-ros-桌面完整版-desktop-full" aria-label="Permalink to &quot;4.安装 ROS（桌面完整版 Desktop-Full）&quot;">​</a></h3><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki github-dark-dimmed vp-code-dark"><code><span class="line"><span style="color:#adbac7;">sudo apt install ros-melodic-desktop-full</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#24292e;">sudo apt install ros-melodic-desktop-full</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p><img src="https://cn-sy1.rains3.com/dfdfgf/blog/Virtual_machine_ubuntu16-04_with_ROS-kinetic/install_ros_kentic_full1.jpg" alt=""></p><p><img src="https://cn-sy1.rains3.com/dfdfgf/blog/Virtual_machine_ubuntu16-04_with_ROS-kinetic/install_ros_kentic_full11.jpg" alt=""></p><p><img src="https://cn-sy1.rains3.com/dfdfgf/blog/Virtual_machine_ubuntu16-04_with_ROS-kinetic/install_ros_kentic_full2.jpg" alt=""></p><p><img src="https://cn-sy1.rains3.com/dfdfgf/blog/Virtual_machine_ubuntu16-04_with_ROS-kinetic/install_ros_kentic_full3.jpg" alt=""></p><p>独立功能包的安装，其中 PACKAGE 代表功能包的名称</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki github-dark-dimmed vp-code-dark"><code><span class="line"><span style="color:#adbac7;">sudo apt install ros-melodic-PACKAGE</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#24292e;">sudo apt install ros-melodic-PACKAGE</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>例如安装机器人 SLAM 地图建模 gmapping 功能包时，可以使用以下命令进行安装</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki github-dark-dimmed vp-code-dark"><code><span class="line"><span style="color:#adbac7;">sudo apt install ros-melodic-slam-gmapping</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#24292e;">sudo apt install ros-melodic-slam-gmapping</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h3 id="_5-初始化-ros" tabindex="-1">5.初始化 ROS <a class="header-anchor" href="#_5-初始化-ros" aria-label="Permalink to &quot;5.初始化 ROS&quot;">​</a></h3><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki github-dark-dimmed vp-code-dark"><code><span class="line"><span style="color:#adbac7;">sudo rosdep init</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#24292e;">sudo rosdep init</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p><img src="https://cn-sy1.rains3.com/dfdfgf/blog/Virtual_machine_ubuntu16-04_with_ROS-kinetic/sudo_rosdep_init_error.jpg" alt=""></p><p>直接创建一个 sources.list.d 文件夹，在目录下新建一个 20-default.list 文件</p><p><img src="https://cn-sy1.rains3.com/dfdfgf/blog/Virtual_machine_ubuntu16-04_with_ROS-kinetic/init_error_save.jpg" alt=""></p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki github-dark-dimmed vp-code-dark"><code><span class="line"><span style="color:#adbac7;">rosdep update</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#24292e;">rosdep update</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p><img src="https://cn-sy1.rains3.com/dfdfgf/blog/Virtual_machine_ubuntu16-04_with_ROS-kinetic/rosdep_update_error.jpg" alt=""></p><p>如遇到 rosdep update 时出现错误，可以参考<a href="https://blog.csdn.net/mrh1714348719/article/details/103803110?utm_source=app" target="_blank" rel="noreferrer">CSDN 大佬文章</a></p><p><img src="https://cn-sy1.rains3.com/dfdfgf/blog/Virtual_machine_ubuntu16-04_with_ROS-kinetic/rosdep_update.jpg" alt=""></p><h3 id="_6-配置环境变量" tabindex="-1">6.配置环境变量 <a class="header-anchor" href="#_6-配置环境变量" aria-label="Permalink to &quot;6.配置环境变量&quot;">​</a></h3><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki github-dark-dimmed vp-code-dark"><code><span class="line"><span style="color:#adbac7;">echo &quot;source /opt/ros/kinetic/setup.bash&quot; &gt;&gt; ~/.bashrc</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#24292e;">echo &quot;source /opt/ros/kinetic/setup.bash&quot; &gt;&gt; ~/.bashrc</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki github-dark-dimmed vp-code-dark"><code><span class="line"><span style="color:#adbac7;">source ~/.bashrc</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#24292e;">source ~/.bashrc</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h3 id="_7-使用测试" tabindex="-1">7.使用测试 <a class="header-anchor" href="#_7-使用测试" aria-label="Permalink to &quot;7.使用测试&quot;">​</a></h3><p>打开 terminal 输入</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki github-dark-dimmed vp-code-dark"><code><span class="line"><span style="color:#adbac7;">roscore</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#24292e;">roscore</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p><img src="https://cn-sy1.rains3.com/dfdfgf/blog/Virtual_machine_ubuntu16-04_with_ROS-kinetic/roscore.jpg" alt=""></p><p>运行 turtlesim 程序</p><p><img src="https://cn-sy1.rains3.com/dfdfgf/blog/Virtual_machine_ubuntu16-04_with_ROS-kinetic/rosrun_rurtlesim.jpg" alt=""></p><p><img src="https://cn-sy1.rains3.com/dfdfgf/blog/Virtual_machine_ubuntu16-04_with_ROS-kinetic/rosrun_turtlesim_turtle_teleop_key.jpg" alt=""></p><h2 id="致谢" tabindex="-1">致谢 <a class="header-anchor" href="#致谢" aria-label="Permalink to &quot;致谢&quot;">​</a></h2><p>OK！本期关于虚拟机 ubuntu16.04 与 ROS-kinetic 的安装就到此为止。接下来后续内容会分几期更新。喜欢的话请支持、转发、订阅！同时也欢迎各位大佬指出不足之处！在此本人万分感谢！</p><p>(注明：本文中的方法在撰写过程中得益于对各位前辈大佬的经验进行借鉴和吸收)</p>',60);function v(s,f,C,q,F,w){const l=i,p=r("ClientOnly");return t(),c("div",null,[k,d(p,null,{default:u(()=>{var a,e;return[(((a=s.$frontmatter)==null?void 0:a.aside)??!0)&&(((e=s.$frontmatter)==null?void 0:e.showArticleMetadata)??!0)?(t(),m(l,{key:0,article:s.$frontmatter},null,8,["article"])):g("",!0)]}),_:1}),y])}const D=o(_,[["render",v]]);export{R as __pageData,D as default};
